import React, { useEffect, useState } from "react";
import "./App.css";
import { stat } from "fs";

interface Prompt {
  id: string;
  title: string;
  description: string;
  temprature: number;
  top_p: number;
  max_tokens: number;
  threshold: number;
  status: string;
}

export default function App() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [temprature, setTemprature] = useState<number | null>(null);
  const [top_p, setTop_p] = useState<number | null>(null);
  const [max_tokens, setMax_tokens] = useState<number | null>(null);
  const [threshold, setThreshold] = useState<number | null>(null);
  const [status, setStatus] = useState("");
  const [prompts, setPrompts] = useState<Prompt[]>([]);
  const [selected, setSelected] = useState<Prompt | null>(null);

  function reset() {
    setTitle("");
    setDescription("");
    setTemprature(null);
    setTop_p(null);
    setMax_tokens(null);
    setThreshold(null);
    setStatus("");
    setSelected(null);
    return;
  }

  useEffect(() => {
    const fetchPrompts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/prompts");
        const prompts: Prompt[] = await response.json();
        setPrompts(prompts);
      } catch (e) {
        console.log(e);
      }
    };
    fetchPrompts();
  }, []);

  function handleSelect(prompt: Prompt) {
    setSelected(prompt);
    setTitle(prompt.title);
    setDescription(prompt.description);
    setTemprature(prompt.temprature);
    setTop_p(prompt.top_p);
    setMax_tokens(prompt.max_tokens);
    setThreshold(prompt.threshold);
    setStatus(prompt.status);
  }

  const handleEdit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!selected) {
      return;
    }
    const promptId: string = selected.id;
    try {
      const response = await fetch(
        `http://localhost:5000/api/prompts/${promptId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            title,
            description,
            temprature,
            top_p,
            max_tokens,
            threshold,
            status,
          }),
        }
      );

      const newPrompt: Prompt = await response.json();

      for (let index = 0; index < prompts.length; index++) {
        const element = prompts[index];
        if (element.id === selected.id) {
          prompts[index] = newPrompt;
        }
      }
      reset()
    } catch (error) {
      console.log(error);
    }
  };

  function handleX(id: string, event: React.MouseEvent) {
    event.stopPropagation();

    try {
      fetch(`http://localhost:5000/api/prompts/${id}`, {
        method: "DELETE",
      });
      const updatedPrompts = prompts.filter((prompt) => prompt.id !== id);
      if (selected && selected.id === id) {
        reset()
      }
      setPrompts(updatedPrompts);
    } catch (error) {
      console.log(error);
    }
  }

  const handleAdd = async (event: React.FormEvent) => {
    event.preventDefault();

    console.log(title);
    console.log(description);
    console.log(temprature);
    console.log(top_p);
    console.log(max_tokens);
    console.log(threshold);
    console.log(status);

    try {
      const response = await fetch("http://localhost:5000/api/prompts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          description,
          temprature,
          top_p,
          max_tokens,
          threshold,
          status,
        }),
      });

      const newPrompt: Prompt = await response.json();
      setPrompts([newPrompt, ...prompts]);
      reset()
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="app-container">
      <div className="prompts-container">
        <button className="new-prompt-button" onClick={reset}> <i className="fas fa-pencil"></i>  New Prompt</button>
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                    <h2>{prompt.title}</h2>
                    <button className="close-btn" onClick={(event) => handleX(prompt.id, event)}>
                      x
                    </button>
                </div>
                <p>{prompt.description}</p>
              </div>
            ))}
      </div>

      <form
        className="prompt-form"
        onSubmit={selected ? handleEdit : handleAdd}>
        <h2>Title</h2>
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        />
        <h2>Description</h2>
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          placeholder="Description"
          rows={10}
          required
        />
        <div className="form-row">
              <div className="col"><h2>Temperature:</h2><input value={temprature !== null ? temprature : ''} onChange={(event) => setTemprature(parseFloat(event.target.value))} type="number" step="0.01" min="0" max="1" name="temperature" className="form-control" placeholder="0 to 1" required/></div>
              <div className="col"><h2>Top_p:</h2><input value={top_p !== null ? top_p : ''} onChange={(event) => setTop_p(parseFloat(event.target.value))} type="number" step="0.01" min="0" max="1" name="top_p" className="form-control" placeholder="0 to 1" required/></div>
              <div className="col"><h2>Max Tokens:</h2><input value={max_tokens !== null ? max_tokens : ''} onChange={(event) => setMax_tokens(parseInt(event.target.value))} type="number" name="max_tokens" min="0" className="form-control" placeholder="min 0" required/></div>
              <div className="col"><h2>Threshold:</h2><input value={threshold !== null ? threshold : ''} onChange={(event) => setThreshold(parseFloat(event.target.value))} type="number" step="0.01" min="0" max="1" name="threshold" className="form-control" placeholder="0 to 1" required/></div>
              <div className="col">
                  <h2>Status:</h2>
                  <select value={status} onChange={(event) => setStatus(event.target.value)} name="status" className="form-control status-select" required>
                        <option value="" selected disabled>Select Status</option>
                        <option value="Yet to Evaluate">Yet to Evaluate</option>
                        <option value="evaluating">Evaluating</option>
                        <option value="best_prompt">Best Prompt</option>
                  </select>
              </div>
              <div className="col">
                <div className="fav-switch">
                  <input type="checkbox"/>
                  <span className="slider round"></span>
                </div>
              </div>
        </div>

        {selected ? (
          <div className="edit-buttons">
            <button type="submit">Save</button>
            <button onClick={reset}>Cancel</button>
          </div>
        ) : (
          <button type="submit">Add</button>
        )}
      </form>
      <div className="favorites-container">
        <div className="fav-header">
          Favorites
          <span role="img" aria-label="heart">
            ❤️
          </span>{" "}
        </div>
        <div className="favorite-prompt-item">
          <h2>title</h2>
          <p>description</p>
        </div>
      </div>
    </div>
  );
}
