import React, { useEffect, useState } from "react";
import "./App.css";

interface Prompt {
  id: string;
  title: string;
  description: string;
  parameters: any;
}

export default function App() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [prompts, setPrompts] = useState<Prompt[]>([]);
  const [selected, setSelected] = useState<Prompt | null>(null);

  useEffect(() => {
    const fetchPrompts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/prompts");
        const prompts: Prompt[] = await response.json();
        setPrompts(prompts);
      } catch (e) {
        console.log(e);
      }
    };
    fetchPrompts();
  }, []);

  function handleSelect(prompt: Prompt) {
    setSelected(prompt);
    setTitle(prompt.title);
    setDescription(prompt.description);
  }

  const handleEdit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!selected) {
      return;
    }
    const promptId: string = selected.id;
    try {
      const response = await fetch(
        `http://localhost:5000/api/prompts/${promptId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            title,
            description,
          }),
        }
      );

      const newPrompt: Prompt = await response.json();

      for (let index = 0; index < prompts.length; index++) {
        const element = prompts[index];
        if (element.id === selected.id) {
          prompts[index] = newPrompt;
        }
      }
      setTitle("");
      setDescription("");
      setSelected(null);
    } catch (error) {
      console.log(error);
    }
  };

  function handleCancel() {
    setTitle("");
    setDescription("");
    setSelected(null);
  }
  function handleX(id: string, event: React.MouseEvent) {
    event.stopPropagation();

    try {
      fetch(`http://localhost:5000/api/prompts/${id}`, {
        method: "DELETE",
      });
      const updatedPrompts = prompts.filter((prompt) => prompt.id !== id);
      if (selected && selected.id === id) {
        setDescription("");
        setTitle("");
        setSelected(null);
      }
      setPrompts(updatedPrompts);
    } catch (error) {
      console.log(error);
    }
  }

  const handleAdd = async (event: React.FormEvent) => {
    event.preventDefault();

    console.log(title);
    console.log(description);
    try {
      const response = await fetch("http://localhost:5000/api/prompts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          description,
        }),
      });

      const newPrompt: Prompt = await response.json();
      setPrompts([newPrompt, ...prompts]);
      setTitle("");
      setDescription("");
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="app-container">
      <div className="prompts-container">
        <button className="new-prompt-button"> <i className="fas fa-pencil"></i>  New Prompt</button>
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt" onClick={() => handleSelect(prompt)}>
                {/* <div className="prompts-header">
                  <button onClick={(event) => handleX(prompt.id, event)}>
                    x
                  </button>
                </div> */}
                <h2>{prompt.title}</h2>
                <p>{prompt.description}</p>
              </div>
            ))}
      </div>

      <form
        className="prompt-form"
        onSubmit={selected ? handleEdit : handleAdd}>
        <h2>Title</h2>
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        />
        <h2>Description</h2>
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          placeholder="Description"
          rows={10}
          required
        />
        <div className="form-row">
                                <div className="col"><label htmlFor="temperature">Temperature:</label><input type="number" step="0.01" min="0" max="1" name="temperature" class="form-control" placeholder="Set Temperature" /></div>
                                <div className="col"><label for="top_p">Top_p:</label><input type="number" step="0.01" min="0" max="1" name="top_p" class="form-control" placeholder="top_p" /></div>
                                <div className="col"><label for="max_tokens">Max Tokens:</label><input type="number" name="max_tokens" class="form-control" placeholder="Max Tokens" /></div>
                                <div className="col"><label for="threshold">Threshold:</label><input type="number" step="0.01" min="0" max="1" name="threshold" class="form-control" placeholder="Threshold" /></div>
                                <div className="col">
                                    <label for="status">Status:</label>
                                    <select name="status" class="form-control status-select">
                                        <option value="" selected disabled>Select Status</option>
                                        <option value="Yet to Evaluate">Yet to Evaluate</option>
                                        <option value="evaluating">Evaluating</option>
                                        <option value="best_prompt">Best Prompt</option>

                                    </select>
                                </div>
                            </div>

        {selected ? (
          <div className="edit-buttons">
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          <button type="submit">Add</button>
        )}
      </form>
     {/* <div className="prompts-grid">
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt-item" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                  <button onClick={(event) => handleX(prompt.id, event)}>
                    x
                  </button>
                </div>
                <h2>{prompt.title}</h2>
                <p>{prompt.description}</p>
              </div>
            ))} 
      </div> */}
      <div className="favorites-container">
        <div className="fav-header">
          Favorites
          <span role="img" aria-label="heart">
            ❤️
          </span>{" "}
        </div>
        <div className="favorite-prompt-item">
          <h2>title</h2>
          <p>description</p>
        </div>
      </div>
    </div>
  );
}
