import React, { useEffect, useState } from "react";
import "./App.css";

interface Prompt {
  id: string;
  title: string;
  description: string;
  parameters: any
}

export default function App() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [prompts, setPrompts] = useState<Prompt[]>([]);
  const [selected, setSelected] = useState<Prompt | null>(null);

  useEffect(() => {
    const fetchPrompts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/prompts");
        const prompts: Prompt[] = await response.json();
        setPrompts(prompts);
      } catch (e) {
        console.log(e);
      }
    };
    fetchPrompts();
  }, []);

  function handleSelect(prompt: Prompt) {
    setSelected(prompt);
    setTitle(prompt.title);
    setDescription(prompt.description);
  }

  const handleEdit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!selected) {
      return;
    }
    const promptId: string = selected.id;
    try {
      const response = await fetch(
        `http://localhost:5000/api/prompts/${promptId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            title,
            description,
          }),
        }
      );

      const newPrompt: Prompt = await response.json();

      for (let index = 0; index < prompts.length; index++) {
        const element = prompts[index];
        if (element.id === selected.id) {
          prompts[index] = newPrompt;
        }
      }
      setTitle("");
      setDescription("");
      setSelected(null);
    } catch (error) {
      console.log(error);
    }
  };

  function handleCancel() {
    setTitle("");
    setDescription("");
    setSelected(null);
  }
  function handleX(id: string, event: React.MouseEvent) {
    event.stopPropagation();

    try {
      fetch(`http://localhost:5000/api/prompts/${id}`, {
        method: "DELETE",
      });
      const updatedPrompts = prompts.filter((prompt) => prompt.id !== id);
      if (selected && selected.id === id) {
        setDescription("");
        setTitle("");
        setSelected(null);
      }
      setPrompts(updatedPrompts);
    } catch (error) {
      console.log(error);
    }
  }

  const handleAdd = async (event: React.FormEvent) => {
    event.preventDefault();

    console.log(title);
    console.log(description);
    try {
      const response = await fetch("http://localhost:5000/api/prompts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          description,
        }),
      });

      const newPrompt: Prompt = await response.json();
      setPrompts([newPrompt, ...prompts]);
      setTitle("");
      setDescription("");
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="app-container">
      <div className="prompts-container">
      {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt-item" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                  <button onClick={(event) => handleX(prompt.id, event)}>
                    x
                  </button>
                </div>
                <h2>{prompt.title}</h2>
                <p>{prompt.description}</p>
              </div>
            ))}
      </div>
      <form
        className="prompt-form"
        onSubmit={selected ? handleEdit : handleAdd}
      >
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        />
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          placeholder="Description"
          rows={10}
          required
        />
        {selected ? (
          <div className="edit-buttons">
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          <button type="submit">Add</button>
        )}
      </form>
      <div className="prompts-grid">
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt-item" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                  <button onClick={(event) => handleX(prompt.id, event)}>
                    x
                  </button>
                </div>
                <h2>{prompt.title}</h2>
                <p>{prompt.description}</p>
              </div>
            ))}
      </div>
      <div className="favorites-sec">
        <div className="fav-header">
          Favorites
          <span role="img" aria-label="heart">
            ❤️
          </span>{" "}
        </div>
        <div className="favorite-prompt-item">
          <div className="prompts-header">
            <button>x</button>
          </div>
          <h2>title</h2>
          <p>description</p>
        </div>
      </div>
    </div>
  );
}
