import React, { useEffect, useState } from "react";
import "./App.css";

interface Prompt {
  id: string;
  title: string;
  description: string;
  temprature: number;
  top_p: number;
  max_tokens: number;
  threshold: number;
  status: string;
}

export default function App() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [temprature, setTemprature] = useState(0);
  const [top_p, setTop_p] = useState(0);
  const [max_tokens, setMax_tokens] = useState(0);
  const [threshold, setThreshold] = useState(0);
  const [status, setStatus] = useState("");
  const [prompts, setPrompts] = useState<Prompt[]>([]);
  const [selected, setSelected] = useState<Prompt | null>(null);

  useEffect(() => {
    const fetchPrompts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/prompts");
        const prompts: Prompt[] = await response.json();
        setPrompts(prompts);
      } catch (e) {
        console.log(e);
      }
    };
    fetchPrompts();
  }, []);

  function handleSelect(prompt: Prompt) {
    setSelected(prompt);
    setTitle(prompt.title);
    setDescription(prompt.description);
    setTemprature(prompt.temprature);
    setTop_p(prompt.top_p);
    setMax_tokens(prompt.max_tokens);
    setThreshold(prompt.threshold);
    set
  }

  const handleEdit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!selected) {
      return;
    }
    const promptId: string = selected.id;
    try {
      const response = await fetch(
        `http://localhost:5000/api/prompts/${promptId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            title,
            description,
          }),
        }
      );

      const newPrompt: Prompt = await response.json();

      for (let index = 0; index < prompts.length; index++) {
        const element = prompts[index];
        if (element.id === selected.id) {
          prompts[index] = newPrompt;
        }
      }
      setTitle("");
      setDescription("");
      setSelected(null);
    } catch (error) {
      console.log(error);
    }
  };

  function handleNewPrompt() {
    setTitle("");
    setDescription("");
    setSelected(null);
  }

  function handleCancel() {
    setTitle("");
    setDescription("");
    setSelected(null);
  }
  function handleX(id: string, event: React.MouseEvent) {
    event.stopPropagation();

    try {
      fetch(`http://localhost:5000/api/prompts/${id}`, {
        method: "DELETE",
      });
      const updatedPrompts = prompts.filter((prompt) => prompt.id !== id);
      if (selected && selected.id === id) {
        setDescription("");
        setTitle("");
        setSelected(null);
      }
      setPrompts(updatedPrompts);
    } catch (error) {
      console.log(error);
    }
  }

  const handleAdd = async (event: React.FormEvent) => {
    event.preventDefault();

    console.log(title);
    console.log(description);
    try {
      const response = await fetch("http://localhost:5000/api/prompts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          description,
        }),
      });

      const newPrompt: Prompt = await response.json();
      setPrompts([newPrompt, ...prompts]);
      setTitle("");
      setDescription("");
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="app-container">
      <div className="prompts-container">
        <button className="new-prompt-button" onClick={handleNewPrompt}> <i className="fas fa-pencil"></i>  New Prompt</button>
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                    <h2>{prompt.title}</h2>
                    <button className="close-btn" onClick={(event) => handleX(prompt.id, event)}>
                      x
                    </button>
                </div>
                <p>{prompt.description}</p>
              </div>
            ))}
      </div>

      <form
        className="prompt-form"
        onSubmit={selected ? handleEdit : handleAdd}>
        <h2>Title</h2>
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        />
        <h2>Description</h2>
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          placeholder="Description"
          rows={10}
          required
        />
        <div className="form-row">
              <div className="col"><h2>Temperature:</h2><input type="number" step="0.01" min="0" max="1" name="temperature" className="form-control" placeholder="0 to 1" /></div>
                  <div className="col"><h2>Top_p:</h2><input type="number" step="0.01" min="0" max="1" name="top_p" className="form-control" placeholder="0 to 1" /></div>
                  <div className="col"><h2>Max Tokens:</h2><input type="number" name="max_tokens" min="0" className="form-control" placeholder="min 0" /></div>
                  <div className="col"><h2>Threshold:</h2><input type="number" step="0.01" min="0" max="1" name="threshold" className="form-control" placeholder="0 to 1" /></div>
                  <div className="col">
                      <h2>Status:</h2>
                      <select name="status" className="form-control status-select">
                        <option value="" selected disabled>Select Status</option>
                        <option value="Yet to Evaluate">Yet to Evaluate</option>
                        <option value="evaluating">Evaluating</option>
                        <option value="best_prompt">Best Prompt</option>

                      </select>
                  </div>
        </div>

        {selected ? (
          <div className="edit-buttons">
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          <button type="submit">Add</button>
        )}
      </form>
     {/* <div className="prompts-grid">
        {!prompts
          ? null
          : prompts.map((prompt) => (
              <div className="prompt-item" onClick={() => handleSelect(prompt)}>
                <div className="prompts-header">
                  <button onClick={(event) => handleX(prompt.id, event)}>
                    x
                  </button>
                </div>
                <h2>{prompt.title}</h2>
                <p>{prompt.description}</p>
              </div>
            ))} 
      </div> */}
      <div className="favorites-container">
        <div className="fav-header">
          Favorites
          <span role="img" aria-label="heart">
            ❤️
          </span>{" "}
        </div>
        <div className="favorite-prompt-item">
          <h2>title</h2>
          <p>description</p>
        </div>
      </div>
    </div>
  );
}
